package week.day1;

public class EvenOrOdd {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
	 for(int i=1;i<=50;i++) {
		 
		 if(i%2==0) {
			 
			 System.out.println("Number is even: " +i);
			 
			 
		 }
		 
		 else {
			 
			 System.out.println("Number is Odd: " +i);
			 
			
		 }
	 }

	}

}
